scripts.fetchUserProfile:
function(accessToken, ctx, cb) {


  request.get("https://api.23andme.com/1/user/", {
    headers: {
      Authorization: 'Bearer ' + accessToken
    }
  }, function(e, r, b) {
    if (e) return cb(e);

    var profile = JSON.parse(b);

    return cb(null, profile);
  });
}
