scripts.fetchUserProfile:
function(accessToken, ctx, callback) {
    request.get('https://app.asana.com/api/1.0/users/me', {
        'headers': {
            'Authorization': 'Bearer ' + accessToken,
            'User-Agent': 'Auth0'
        }
    }, function (e, r, b) {
        if (e) return callback(e);
        if (r.statusCode !== 200) return callback(new Error('StatusCode:' + r.statusCode));
        var user = JSON.parse(b).data;
        var profile = {
            id: user.id,
            name: user.name,
            email: user.email,
            picture: user.photo["image_128x128"]
        }
        callback(null, profile);
    });
}
