scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get(
    'https://api.bufferapp.com/1/user.json', {
      headers: {
        'Authorization': 'Bearer ' + accessToken
      }
    },
    function(e, r, b) {
      if (e)
        return cb(e);
      if (r.statusCode !== 200)
        return cb(new Error(r));
      var profile = JSON.parse(b);
      cb(null, {
        user_id: profile.id,
        nickname: profile.name
      });
    }
  );
}
