scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('https://discordapp.com/api/users/@me', {
    'headers': {
      'Authorization': 'Bearer ' + accessToken,
      'User-Agent': 'Auth0'
    }
  }, function(e, r, b) {
    if (e) {
      return callback(e);
    }
    if (r.statusCode !== 200) {
      return callback(new Error('StatusCode:' + r.statusCode));
    }
    //callback(null, JSON.parse(b));
    cb(null, JSON.parse(b));
  });
  // call oauth2 APIwith the accesstoken and create the profile
}
