scripts.fetchUserProfile:
function(accessToken, ctx, callback) {
  request.get('https://discordapp.com/api/v6/users/@me', {
    'headers': {
      'Authorization': 'Bearer ' + accessToken,
      'User-Agent': 'Auth0'
    }
  }, function (e, r, b) {
    if (e) {
      return callback(e);
    }
    if (r.statusCode !== 200) {
      return callback(new Error('StatusCode:' + r.statusCode));
    }

    callback(null, JSON.parse(b));
  });
}
