scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('https://login-sandbox.ethosgroup.com/connect/userinfo', {
    headers: {
      'Authorization': 'Bearer ' + accessToken
    }
  }, function(e, r, b) {
    if (e) return cb(e);
    if (r.statusCode !== 200) return cb(new Error('StatusCode: ' + r.statusCode));
    var profile = JSON.parse(b);
    cb(null, {
      user_id: profile.uid,
      email: profile.email,
      email_verified: profile.email_verified,
      name: profile.name,
      account: profile.team,
      family_name: profile.name_details.surname,
      given_name: profile.name_details.given_name
    });
  });
}
