scripts.fetchUserProfile:
function(accessToken, ctx, cb) {

  request.get('https://api.fitbit.com/1/user/-/profile.json', {
      headers: {

        'Authorization': 'Bearer ' + accessToken
      }
    }

    ,
    function(err, r, body) {
      if (err) {
        return cb(err);
      }
      try {
        var profile = JSON.parse(body);
        profile.provider = 'fitbit';
        profile.id = profile.user.encodedId;
        profile.displayName = profile.user.displayName;
        profile.name = profile.user.fullName;
        cb(null, profile);
      } catch (e) {
        cb(e);
      }
    });


}
