scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('https://api.foursquare.com/v2/users/self?oauth_token=' + accessToken + '&v=20160914', {}, function(e, r, b) {
    if (e) return cb(e);
    if (r.statusCode !== 200) return cb(new Error('StatusCode: ' + r.statusCode));
    profile = JSON.parse(b)
    if (profile.meta.code != 200) return cb(new Error('profile StatusCode: ' + profile.meta.code));
    profile.user_id = profile.response.user.id;
    cb(null, profile);
  });
}
