scripts.fetchUserProfile:
function(accessToken, ctx, cb) {

  console.log(accessToken);
  // call oauth2 APIwith the accesstoken and create the profile 
  request.get('https://api.dev-t-syd.honcho.be/auth/social-connections/get-user-details', {
    'headers': {
      'Authorization': 'Bearer ' + accessToken
    }
  }, function(e, r, b) {

    if (e) return cb(e);
    if (r.statusCode !== 200) return cb(new Error('StatusCode:' + r.statusCode));
    var providerResponse = JSON.parse(b);
    if (!providerResponse) return cb(new Error(b));
    console.log(providerResponse);

    cb(null, providerResponse, ctx);
  });
}
