scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('https://api.line.me/v2/profile', {
    'headers': {
      'Authorization': 'Bearer ' + accessToken,
    }
  }, function(e, r, b) {
    if (e) return cb(e);
    if (r.statusCode !== 200) return cb('StatusCode:' + r.statusCode);
    cb(null, JSON.parse(b, 0, 2));
  });
}
