scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('https://openapi.naver.com/v1/nid/me', {
    headers: {
      'Authorization': 'Bearer ' + accessToken
    }
  }, function(e, r, b) {
    if (e) return cb(e);
    if (r.statusCode !== 200) return cb(new Error('StatusCode: ' + r.statusCode));
    var body = JSON.parse(b);
    var profile = body.response;
    var name = profile.name || profile.nickname || profile.email; 
    cb(null, {
      user_id: profile.id,
      email: profile.email,
      email_verified: true,
      nickname: profile.nickname,
      picture: profile.profile_image,
      name: name,     
      app_metadata: {
        authorization: {
          roles: ['User']
        }
      },
      // Additional user information
      // age: profile.age,
      // gender: profile.gender,
      // birthday: profile.birthday, 
    });
  });
}
