scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('https://proveid-test6.prove.id/oauth2/userinfo', {
    headers: {
      'Authorization': 'Bearer ' + accessToken
    }
  }, function(e, r, b) {
    if (e) return cb(e);
    if (r.statusCode !== 200) return cb(new Error('StatusCode: ' + r.statusCode));
    var payload = b.split('.')[1];
    var profile = JSON.parse(Buffer.from(payload, 'base64'));
    cb(null, profile);
  });
}
