scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('http://cs1.cogniva.solutions:8002/otdsws/v1/currentuser', {
    headers: {
      'Authorization': 'Bearer ' + accessToken
    }
  }, function(e, r, b) {
    if (e) return cb(e);
    if (r.statusCode !== 200) return cb(new Error('StatusCode: ' + r.statusCode));
    var profile = JSON.parse(b);
    profile.user_id = profile.user.uuid;
    profile.description = profile.user.description;
    profile.name = profile.user.id;
    cb(null, profile);
  });
}
