scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('https://maam-dev.axa.com/maam/v2/userinfo', {
    headers: {
      'Authorization': 'Bearer ' + accessToken
    }
  }, function(e, r, b) {
    if (e) return cb(e);
    if (r.statusCode !== 200) return cb(new Error('StatusCode: ' + r.statusCode + '. Token: ' + accessToken));

    var profile = JSON.parse(b);
    profile.user_id = profile.sub;
    cb(null, profile);
  });
}
