scripts.fetchUserProfile:
function(accessToken, context, callback) {
  request.get('https://api.pinterest.com/v1/me/?access_token=' + accessToken, function(err, response, body) {
    if (err) {
      return callback(err);
    }

    if (response.statusCode !== 200) {
      return callback(new Error('StatusCode: ' + response.statusCode));
    }

    callback(null, JSON.parse(body));
  });
}
