scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  // call oauth2 APIwith the accesstoken and create the profile
request.get('https://accounts.platform.intuit.com/v1/openid_connect/userinfo', {
        'headers': {
            'Authorization': 'Bearer ' + accessToken,
            'User-Agent': 'Auth0'
        }
    }, function (e, r, b) {
        if (e) {
            return cb(e);
        }
        if (r.statusCode !== 200) {
            return cb(new Error('StatusCode:' + r.statusCode));
        }
        cb(null, JSON.parse(b));
    });
}
