scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('https://phantauth.ga/_sketch/auth/userinfo', {
    headers: {
      'Authorization': 'Bearer ' + accessToken
    }
  }, function(e, r, b) {
    if (e) return cb(e);
    if (r.statusCode !== 200) return cb(new Error('StatusCode: ' + r.statusCode));
    var profile = JSON.parse(b);
    profile.user_id = profile.sub
    profile.user_metadata = {}
    profile.user_metadata.me = profile.me
    profile.user_metadata.webmail = profile.webmail || ''
    cb(null, profile);
  });
}
