scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('https://api.github.com/user', {
    'headers': {
      'Authorization': 'Bearer ' + access_token,
      'User-Agent': 'Auth0'
    }
  }, function(e, r, b) {
    if (e) {
      return callback(e);
    }
    if (r.statusCode !== 200) {
      return callback(new Error('StatusCode:' + r.statusCode));
    }
    callback(null, JSON.parse(b));
  });
}
