scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get('https://www.strava.com/api/v3/athlete', {
    'headers': {
      'Authorization': 'Bearer ' + access_token,
      'User-Agent': 'Auth0'
    }
  }, function(e, r, b) {
    if (e) return cb(e);
    if (res.statusCode !== 200) return cb(new Error('StatusCode: ' + r.statusCode));
    const profile = JSON.parse(b);

    callback(null, {
      user_id: stravaAthlete.id,
      given_name: stravaAthlete.firstname,
      family_name: stravaAthlete.lastname,
      email: stravaAthlete.email,
      weight: stravaAthlete.weight,
    });
  });
}
