scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  var request = require("request");
	// Use ctx, stripe does not have a 
  // url like graph.facebook.com/me 

	const actId = ctx.stripe_user_id;
  const url = `https://api.stripe.com/v1/accounts/${actId}`

  request.get(url, {
    auth: {
      bearer: accessToken
    },
    json: true,
  }, function(error, response, profile){
    if( error ){
      cb(error);
    }
    cb(null, profile);
  });
}
