scripts.fetchUserProfile:
function(access_token, ctx, callback) {
  'use strict';

  const jsonwebtoken = require('jsonwebtoken');

  if (!ctx.id_token) {
    return callback('missing-id_token');
  }

  const jwt = jsonwebtoken.decode(ctx.id_token);

  if (!jwt)
    return callback('malformed-id_token');

  if (!jwt.sub)
    return callback('missing-sub');

  if (!jwt.email)
    return callback('missing-email');


  const profile = {
    user_id: jwt.sub,
    email: jwt.email,
    user_metadata: {},
    app_metadata: {}
  };

  if (jwt.given_name && jwt.family_name) profile.name = jwt.given_name + ' ' + jwt.family_name;
  if (jwt.given_name) profile.given_name = jwt.given_name;
  if (jwt.family_name) profile.family_name = jwt.family_name;

  return callback(null, profile);
}
